/**
*  @file beloader-animations.js
*  @author Liqueur de Toile <contact@liqueurdetoile.com>
*  @licence AGPL-3.0 {@link https://github.com/liqueurdetoile/beloader-animations/blob/master/LICENSE}
*/

import 'core/publicpath';

export const animations = {
  init: function (options = {}) {
    let elementify, animejs;

    // Load elementify and anime if needed
    if (!window.elementify && options.elementify !== false) {
      elementify = this.parent.fetch('script', {
        id: 'elementify',
        url: options.elementify || 'https://unpkg.com/elementify@latest'
      }).promise;
    } else elementify = new Promise((resolve, reject) => { resolve();});

    if (!window.anime && options.anime !== false) {
      animejs = this.parent.fetch('script', {
        id: 'anime',
        url: options.anime || 'https://unpkg.com/animejs@latest'
      }).promise;
    } else animejs = new Promise((resolve, reject) => { resolve();});

    this.promise = Promise.all([elementify, animejs]);

    return this;
  },

  load: function (animation, success = null, failure = null) {
    let p = import(
      /* webpackChunkName: "[request]" */
      `animations/${animation}`
    );

    return new Promise((resolve, reject) => {
      p.then(
        a => {
          if (success instanceof Function) success(a.default);
          resolve(a.default);
        },
        e => {
          if (failure instanceof Function) failure(e);
          reject(e);
        }
      );
    });
  }
};

export default animations;

import ObjectArray from 'dot-object-array';
import uniqid from 'uniqid';

/**
*  Abstract class for animations
*
*  @version 1.0.0
*  @since 1.0.0
*  @author Liqueur de Toile <contact@liqueurdetoile.com>
*/
export default class AbstractAnimation {
  /**
  *  Generates unique ID and stores options
  *
  *  @version 1.0.0
  *  @since 1.0.0
  *  @author Liqueur de Toile <contact@liqueurdetoile.com>
  *  @abstract
  */
  constructor(options = {}) {
    /**
    *  Auto-generated unique id for animation
    *  @since 1.0.0
    *  @type {string}
    */
    this.id = 'animation-' + uniqid();

    /**
    *  Options for the animation
    *  @since 1.0.0
    *  @type {DotObjectArray}
    */
    this.options = new ObjectArray(options);

    /**
    *  Animejs animation object or timeline
    *  @type {object}
    */
    this.animation = undefined;
  }

  /**
  *  Always return false
  *  if not overriden in childs class
  *
  *  @version 1.0.0
  *  @since 1.0.0
  *  @author Liqueur de Toile <contact@liqueurdetoile.com>
  *  @type {boolean}
  */
  get block() {
    return false;
  }

  /**
  *  Check if required options have been provided
  *
  *  @version 1.0.0
  *  @since 1.0.0
  *  @author Liqueur de Toile <contact@liqueurdetoile.com>
  *
  *  @param {Array|string} opts Option name or array of options names
  *  @throws {TypeError}  If an option is missing
  */
  check(opts) {
    if (typeof opts === 'string') opts = [opts];
    opts.forEach(opt => {
      if (!this.options.has(opt)) throw new TypeError(`Missing option ${opt} for animation`);
    });
    return true;
  }
}

/**
*  @file publicpath.js
*  @author Liqueur de Toile <contact@liqueurdetoile.com>
*  @licence AGPL-3.0 {@link https://github.com/liqueurdetoile/beloader-animations/blob/master/LICENSE}
*/

/**
*  This script initialize webpack public path for CDN usage compatibility
*
*  @version 1.0.0
*  @since 1.0.0
*  @author Liqueur de Toile <contact@liqueurdetoile.com>
*/

let scripts = document.getElementsByTagName('script');
/** @ignore */
let r = new RegExp('^(?:[a-z]+:)?//', 'i');

for (let i = 0; i < scripts.length; i++) {
  let script = scripts[i];
  let tmp = script.src.match(/(.+)beloader-animations/);

  // Only set public path if beloader path is absolute
  if (tmp && tmp[1] && r.test(tmp[1])) __webpack_public_path__ = tmp[1]; // eslint-disable-line
}

